for y in range(Cell_Qty): #Для каждого ряда
	for x in range(Cell_Qty):
		cell_all_index[y][x][0] = x*Cell_size
		cell_all_index[y][x][1] = y*Cell_size
		#create | draw cell
		cell = pg.Surface((Cell_size, Cell_size)) # Create Cells
		cell.fill(Gray)
		screen.blit(cell, (x*Cell_size, y*Cell_size)) # Draw Cells
		pg.draw.rect(screen, Black, (x*Cell_size, y*Cell_size, Cell_size, Cell_size), 1)
