run = True
while run:
	pg.display.update()
	for event in pg.event.get():
		if event.type == pg.QUIT:
			pg.quit()
			run = False
		elif event.type == pg.MOUSEBUTTONDOWN:
			number = FNT18.render(str(random.randint(0, 5)), 1, Black)
			screen.blit(number, (x*Cell_size+Cell_Qty//2, y*Cell_size+Cell_Qty//2))
